mapper.insert:
INSERT INTO board (
	title, content, image, user_name, user_password
) VALUES (
	#{title}, #{content}, #{image},	 #{user_name}, #{user_password}
)

mapper.update:
UPDATE board
	SET title = #{title}, content = #{content}, 
		image = #{image}, user_name=#{user_name}, user_password=#{user_password}
WHERE board_id = #{board_id}
